import { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Callback = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const handleAuthCallback = async () => {
      try {
        const queryParams = new URLSearchParams(window.location.search);
        const code = queryParams.get("code");
        if (!code) throw new Error("No authorization code found");

        // Send the code to the backend to exchange it for tokens
        const response = await fetch("http://localhost:8000/api/callback", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ code }),
        });

        if (!response.ok) {
          throw new Error("Failed to exchange code for tokens");
        }

        const tokens = await response.json();

        // Store tokens securely (e.g., in session storage)
        sessionStorage.setItem("accessToken", tokens.access_token);
        sessionStorage.setItem("idToken", tokens.id_token);

        // Navigate to the dashboard
        navigate("/dashboard");
      } catch (error) {
        console.error("Error handling the callback:", error);
        navigate("/error");
      }
    };

    handleAuthCallback();
  }, [navigate]);

  return <div>Loading...</div>;
};

export default Callback;
